INSERT INTO `review` (`review_id`,`product_id`,`open_id`,`review_user_name`,`review_user_img_url`,`score`,`content`,`create_date`,`ref_review_id`) VALUES (200,6,'','sea0923','https://storage.360buyimg.com/i.imageUpload/6c63683039323331333738363432383433353038_sma.jpg',5,'电视盒子一直都用海美迪品牌，很不错，但时间久了卡顿还是存在的，甚至有死机问题，但很好用。现在创维和**联合出了这款网络机顶盒，很好用，尤其是语音功能特别适合老人使用，应用程序随便装，不受限制，相当于一个开放平台，只要能相到的，他都可以为你实现。遥控的手感超级给力，大小合适，操作简单，更重要的是采用蓝牙连接，不再为红外不准无法操作而苦恼，推荐各位友友购买，物超所值。信赖京东！小哥，辛苦了！','2020-01-08','13704031955');
