INSERT INTO `review` (`review_id`,`product_id`,`open_id`,`review_user_name`,`review_user_img_url`,`score`,`content`,`create_date`,`ref_review_id`) VALUES (412,11,'','戴洪智','https://misc.360buyimg.com/user/myjd-2015/css/i/peisong.jpg',5,'很好的产品，单位买来用于教学的，在办公室墙上先试了机，分辨率比以前的商务投影仪高，亮度也不错，因教室还没布置好，阳光不强时不拉窗帘也有不错的体验','2019-06-25','12949509999');
